import React, { useState } from 'react';

// Abstract Factory for creating the steps
const StepFactory = (selectedPackage, name, email, address) => {
  if (!selectedPackage || !name || !email || !address) {
    return null;
  }

  // Step 2: Provide Your Information
  const ProvideInformationStep = () => (
    <div>
      <h2>Step 2: Provide Your Information</h2>
      <form>
        <label>
          Name:
          <input type="text" value={name} onChange={handleNameChange} />
        </label>
        <br />
        <label>
          Email:
          <input type="email" value={email} onChange={handleEmailChange} />
        </label>
        <br />
        <label>
          Address:
          <input type="text" value={address} onChange={handleAddressChange} />
        </label>
        <br />
        <button onClick={handlePurchase}>Purchase</button>
      </form>
    </div>
  );

  // Step 3: Review Your Order
  const ReviewOrderStep = () => (
    <div>
      <h2>Step 3: Review Your Order</h2>
      <p>Selected Package: {selectedPackage}</p>
      <p>Name: {name}</p>
      <p>Email: {email}</p>
      <p>Address: {address}</p>
      <button onClick={() => alert('Order has been confirmed!')}>
        Confirm Order
      </button>
    </div>
  );

  // Step 4: Make Payment
  const MakePaymentStep = () => (
    <div>
      <h2>Step 4: Make Payment</h2>
      <p>Payment methods:</p>
      <ul>
        <li>Credit Card</li>
        <li>PayPal</li>
        <li>Bank Transfer</li>
      </ul>
      <button onClick={() => alert('Payment successful!')}>Pay Now</button>
    </div>
  );

  // Step 5: Booking Confirmation
  const BookingConfirmationStep = () => (
    <div>
      <h2>Step 5: Booking Confirmation</h2>
      <p>Your booking has been confirmed!</p>
      <p>Package: {selectedPackage}</p>
      <p>Name: {name}</p>
      <p>Email: {email}</p>
      <p>Address: {address}</p>
    </div>
  );

  // Step 6: Enjoy Your Trip!
  const EnjoyTripStep = () => (
    <div>
      <h2>Step 6: Enjoy Your Trip!</h2>
      <p>Have a fantastic journey!</p>
    </div>
  );

  return {
    ProvideInformationStep,
    ReviewOrderStep,
    MakePaymentStep,
    BookingConfirmationStep,
    EnjoyTripStep,
  };
};

const PurchasePage = () => {
  const [selectedPackage, setSelectedPackage] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');

  const handlePackageSelection = (packageName) => {
    setSelectedPackage(packageName);
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleAddressChange = (event) => {
    setAddress(event.target.value);
  };

  const handlePurchase = () => {
    // Perform validation on the form inputs
    if (!selectedPackage || !name || !email || !address) {
      alert('Please fill in all the required information.');
      return;
    }

    // Logic for handling the purchase, e.g., submitting the order or redirecting to a payment gateway
    console.log('Purchase completed!');
    console.log('Selected Package:', selectedPackage);
    console.log('Name:', name);
    console.log('Email:', email);
    console.log('Address:', address);

    // Reset the form
    setSelectedPackage('');
    setName('');
    setEmail('');
    setAddress('');
  };

  // Create the appropriate steps based on the current state
  const {
    ProvideInformationStep,
    ReviewOrderStep,
    MakePaymentStep,
    BookingConfirmationStep,
    EnjoyTripStep,
  } = StepFactory(selectedPackage, name, email, address);

  return (
    <div>
      <h2>Step 1: Select a Package</h2>
      <ul>
        <li>
          <button onClick={() => handlePackageSelection('Package A')}>
            Package A
          </button>
        </li>
        <li>
          <button onClick={() => handlePackageSelection('Package B')}>
            Package B
          </button>
        </li>
        <li>
          <button onClick={() => handlePackageSelection('Package C')}>
            Package C
          </button>
        </li>
      </ul>

      {ProvideInformationStep && <ProvideInformationStep />}

      {ReviewOrderStep && <ReviewOrderStep />}

      {MakePaymentStep && <MakePaymentStep />}

      {BookingConfirmationStep && <BookingConfirmationStep />}

      {EnjoyTripStep && <EnjoyTripStep />}
    </div>
  );
};

export default PurchasePage;
