import React, { Component, Fragment } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import '../../asset/css/custom-styles.css';

class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shouldHideMenuItem: false,
      isScrolled: false, // Add state to track scrolling
    };
  }

  componentDidMount() {
    const currentUrl = window.location.href;
    const shouldHideMenuItem = currentUrl.includes('#blog');
    this.setState({ shouldHideMenuItem });

    // Add scroll event listener
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount() {
    // Remove scroll event listener on component unmount
    window.removeEventListener('scroll', this.handleScroll);
  }

  handleScroll = () => {
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    const isScrolled = scrollTop > 0; // Check if scrolling has started
    this.setState({ isScrolled });
  };

  render() {
    const { shouldHideMenuItem, isScrolled } = this.state;

    return (
      <Fragment>
        <Navbar
          fixed="top"
          collapseOnSelect
          expand="lg"
          bg={isScrolled ? 'dark' : 'light'} // Change background color based on scrolling
          variant="dark"
          className={isScrolled ? 'scrolled' : ''} // Add a class for additional styling when scrolling
        >
          <Container>
            <Navbar.Brand href="/" className="solnavbold">
              MLibraian
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="me-auto">
                {!shouldHideMenuItem && <Nav.Link href="#blog">Our Blog</Nav.Link>}
              </Nav>
              <Nav>
                <Nav.Link eventKey={2} href="/hotels">
                  Hotels
                </Nav.Link>
                <Nav.Link eventKey={2} href="/admin/bookingdetails">
                  Bookings
                </Nav.Link>
                <Nav.Link eventKey={2} href="/Login">
                  Login
                </Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </Fragment>
    );
  }
}

export default Menu;
